//SOLUÇÃO
import React from 'react';

interface Produto {
  id: number;
  descricao: string;
}

interface ProdutosState {
  produtos: Produto[];
}

class Produtos extends React.Component<{}, ProdutosState> {
  constructor(props: {}) {
    super(props);
    this.state = { produtos: [] };
  }

  handleAddClick() {
    const { produtos } = this.state;
    const novoProduto: Produto = { id: 0, descricao: 'Banana' };
    produtos.push(novoProduto);
    this.setState(prevState => ({ produtos: [...prevState.produtos, novoProduto] }));
  }
  render() {
    const { produtos } = this.state;
    return (
    <div>
      <ul>
      {produtos.map(p => <li>{p.descricao}</li>)}
      </ul>
      <button onClick={this.handleAddClick}>+</button>
    </div>
    );
  }
}

export default Produtos;
